import React, { useState } from 'react';
import './App.css';

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [messages, setMessages] = useState([
    { role: 'system', text: 'Hello, how can I assist you today?' },
    { role: 'user', text: 'What is the capital of France?' },
    { role: 'system', text: 'The capital of France is Paris.' },
  ]);
  const [inputValue, setInputValue] = useState('');

  const handleSend = () => {
    if (!inputValue.trim()) return;

    // Example: push user message, then a system response
    setMessages([...messages, { role: 'user', text: inputValue.trim() }]);
    setInputValue('');

    // Simulated system response
    setTimeout(() => {
      setMessages((prev) => [
        ...prev,
        { role: 'system', text: 'This is an AI-generated response.' },
      ]);
    }, 1000);
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <div className="app-wrapper">
      {/* Header */}
      <header>
        <div className="logo">Hemang</div>
        <div className="settings-icon" onClick={toggleSidebar}>
          {/* You can replace this with a hamburger icon, gear icon, etc. */}
          {sidebarOpen ? 'Hide Sidebar' : 'Show Sidebar'}
        </div>
      </header>

      {/* Main Container */}
      <div className="main-container">
        {/* Chat Area */}
        <div className="chat-area">
          {/* Chat Messages */}
          <div className="messages">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`chat-message ${msg.role === 'system' ? 'system' : 'user'}`}
              >
                <strong>{msg.role === 'system' ? 'System/AI: ' : 'User: '}</strong>
                {msg.text}
              </div>
            ))}
          </div>

          {/* Chat Input */}
          <div className="input-container">
            <input
              type="text"
              placeholder="Type your query here..."
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyDown={(e) => { if (e.key === 'Enter') handleSend(); }}
            />
            <button className="send-button" onClick={handleSend}>
              Send
            </button>
          </div>
        </div>

        {/* Right Sidebar (toggles using CSS classes) */}
        <div className={`sidebar ${sidebarOpen ? '' : 'hidden'}`}>
          <h3>Additional Info</h3>
          <p><strong>Query Decomposition:</strong></p>
          <p>Breaking down user query into steps...</p>
          <p><strong>References/Links:</strong></p>
          <p>• Source 1: https://example.com</p>
          <p>• Source 2: https://example.org</p>
          <p><strong>Thinking Process:</strong></p>
          <p>Partial chain-of-thought, displayed here...</p>
          <p><strong>Graph (if applicable):</strong></p>
          <p>(Graph placeholder or embedded element)</p>
        </div>
      </div>

      {/* Footer */}
      <footer>
        &copy; 2025 Hemang. All rights reserved.
      </footer>
    </div>
  );
}

export default App;
